mapper.insert:
DROP TABLE IF EXISTS ${fieldReservation};
CREATE TABLE `${fieldReservation}` (
                                       id BIGINT AUTO_INCREMENT PRIMARY KEY,
                                       reservation_id VARCHAR(255) NOT NULL,
                                       reservation_data DATETIME NOT NULL,
                                       reservation_team_Whether VARCHAR(255) NOT NULL,
                                       reservation_team_id VARCHAR(255) NULL,
                                       soccer_field BIGINT NOT NULL,
                                       FOREIGN KEY (soccer_field) REFERENCES soccer_field(id)
);
